INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (19,'Commands','commands','	<p class=\"title\">Commands</p>\r\n	<p>An updated (development version) list of commands can be found in the subversion repository <a href=https://theory5.chem.pitt.edu/svn/mpd/mpd/trunk/doc/COMMANDS>here</a>.</p>\r\n	\r\n','Commands',1,0,1,1,5,2,'2004-06-09 13:09:43','2004-06-11 05:57:21');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (20,'gmpc','gmpc','	<p class=\"title\">gpmc</p>\r\n	<p><b>About</b>:<br>\r\n	Gnome Music Player Client (gmpc).<br>\r\n	For more info, go <a href=http://qballcow.nl/?s=13>here</a>.<br>\r\n	<br>\r\n	<b>Features</b><br>\r\n	* Supports for loading/saving playlists<br>\r\n	* File Browser<br>\r\n	* Browser based on ID3 information<br>\r\n	* Search<br>\r\n	* Current playlist viewer with search<br>\r\n	* ID3 Information<br>\r\n	* Lots more<br>\r\n	<br>\r\n	<b>Screenshots</b>:<br>\r\n	<a href=images/gmpc1.png>gmpc Screenshot 1</a> (3/9/2004)<br>\r\n	<a href=images/gmpc2.png>gmpc Screenshot 2</a> (3/9/2004)<br>\r\n	<br>\r\n	<b>Requirements</b>:<br>\r\n	gmpc requires GTK+ 2.2 or better.  If you only have GTK+ 2.0, then try\r\n	<a href=?page=gtk2mp>gtk2mp</a><br>\r\n	<br>\r\n	<b>Install</b>:<br>\r\n  	1) <a href=?page=files#gmpc>download gmpc</a><br>\r\n  	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n  	4) compile (type \"make\")<br>\r\n	5) install (type \"make install\")<br>\r\n	<br>\r\n	<b>For Help</b>:<br>\r\n	Come to the forums<br>\r\n	<br>\r\n	(c)Copyright 2004 by Qball Cow<br>\r\n	This code is released under the GPL license<br>\r\n	</p>\r\n','gmpc',1,NULL,1,1,2,2,'2004-06-13 23:06:54','2004-06-13 23:06:54');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (21,'ncmpc','ncmpd','	<p class=\"title\">ncmpc</p>\r\n	<p><b>About</b>:<br>\r\n	A ncurses (command line) interface for MPD.<br>\r\n	For more info, go <a href=http://hem.bredband.net/kaw/ncmpc>here</a><br>\r\n	<br>\r\n	<b>Screenshots</b>:<br>\r\n	<a href=\"images/ncmpc_1.png\">ncmpc Screenshot 1</a><br>\r\n	<br>\r\n	<b>Requirements</b>:<br>\r\n	* <a href=\"http://www.gnu.org/software/ncurses/\">ncurses</a><br> \r\n	* <a href=\"http://www.gtk.org\">glib-2</a><br>\r\n	* <a href=\"http://www.rpm.org\">popt</a> (command line parsing library)<br>\r\n	<br>\r\n	<b>Install</b>:<br>\r\n  	1) <a href=?page=files#ncmpc>download ncmpc</a><br>\r\n  	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n  	4) compile (type \"make\")<br>\r\n	5) (Optional) as root: make install<br>\r\n	<br>\r\n	<b>For Help</b>:<br>\r\n	man ncmpc<br>\r\n	<br>\r\n	(c)Copyright 2004 by Kalle Wallin<br>\r\n	This code is released under the GPL license<br>\r\n	</p>\r\n','ncmpd',1,NULL,1,1,2,2,'2004-06-13 23:07:48','2004-06-13 23:07:48');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (22,'mpc','mpc','	<p class=\"title\">mpc</p>\r\n	<p><b>About</b>:<br>\r\n	A command line tool to interface MPD.<br>\r\n	<br>\r\n	<b>Features</b>:<br>\r\n	* Bash tab completion<br>\r\n	* Can pipe output of other commands into mpc<br>\r\n	<br>\r\n	<b>Screenshots</b>:<br>\r\n	<a href=\"images/mpc_20030723_1.jpg\">mpc Screenshot 1</a> (7/23/2003)<br>\r\n	<br>\r\n	<b>Install</b>:<br>\r\n  	1) <a href=?page=files#mpc>download mpc</a><br>\r\n  	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n  	4) compile (type \"make\")<br>\r\n	5) (Optional) as root: make install<br>\r\n	<br>\r\n	<b>Configuration</b>:<br>\r\n	For bash tab completion: $ cat mpc-bashrc >> ~/.bashrc<br>\r\n	Set the environment variables MPD_HOST and MPD_PORT.<br>\r\n	<br>\r\n	<b>For Help</b>:<br>\r\n	man mpc<br>\r\n	<br>\r\n	<b>Thanks</b>:<br>\r\n	mpc is the brainchild of MaxieZ, who wrote a prototype version in perl and did all the bash completion work.<br>\r\n	</p>\r\n','mpc',1,NULL,1,1,2,2,'2004-06-13 23:08:22','2004-06-13 23:08:22');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (23,'kmp','kmp','	<p class=\"title\">kmp</p>\r\n	<p><b>About</b>:<br>\r\n	A graphical client/frontend for MPD written with Qt.<br>\r\n	<br>\r\n	For more info: <a href=\"http://www.threadbox.net/kmp.php\">http://www.threadbox.net/kmp.php</a><br>\r\n	<br>        \r\n	<b>Screenshots</b>:<br>\r\n	<a href=\"images/kmp_1.png\">Screenshot 1</a><br>\r\n	<br>\r\n	<b>Install:</b><br>\r\n	1) <a href=?page=files#kmp>download kmp</a><br>\r\n	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n	4) compile (type \"make\")<br>\r\n	5) (Optional) as root: make install<br>\r\n	<br>\r\n	(c)Copyright 2003 Jason \"utu\" Tuck and Adam \"thread\" Bellinson <br>\r\n	This code is released under the GPL license<br>\r\n	</p>\r\n','kmp',1,NULL,1,1,2,2,'2004-06-13 23:08:52','2004-06-13 23:08:52');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (24,'glurp','glurp','	<p class=\"title\">Glurp</p>\r\n	<p><b>About</b>:<br>\r\n	A GTK2 based graphical client with simple and clean interface.<br>\r\n	For more info, visit <a href=\"http://sourceforge.net/projects/glurp/\">http://sourceforge.net/projects/glurp/</a>.<br>\r\n	<br>\r\n	<b>Features</b><br>\r\n	* Support for loading/saving playlists<br>\r\n	* File Browser<br>\r\n	* Playlist Quicksearch<br>\r\n	* Supports streams in MPD (MPD 0.11.0 and higher)<br>\r\n	* ID3 Information<br>\r\n	* Single window interface, with possibility to hide the playlist<br>\r\n	<br>\r\n	<b>Screenshots</b>:<br>\r\n	<a href=images/glurp1.png>glurp 0.3.0</a> (3/9/2004)<br>\r\n	<a href=images/glurp2.png>glurp 0.3.0</a> (3/9/2004)<br>\r\n	<br>\r\n	<b>Requirements</b>:<br>\r\n	Glurp requires GTK+ 2.4 or better.  If you only have GTK+ 2.2, try <a href=?page=gmpc>gmpc</a><br>.\r\n	<br>\r\n	<b>Install</b>:<br>\r\n  	1) <a href=?page=files#glurp>download glurp</a><br>\r\n  	2) unpack the tarball<br>\r\n	3) configure (type \"./configure\")<br>\r\n  	4) compile (type \"make\")<br>\r\n	5) install (type \"make install\" as root)<br>\r\n	<br>\r\n	<b>For support</b> visit the <a href=http://www.musicpd.org/forum>forums</a>.\r\n	<br>\r\n	(c)Copyright 2004 by Andrej Kacian<br>\r\n	This code is released under the GPL license<br>\r\n	</p>\r\n','glurp',1,NULL,1,1,2,2,'2004-06-13 23:09:26','2004-06-13 23:09:26');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (25,'MPD Commander','mpdc','	<p class=\"title\">MPD Commander</p>\r\n	<p><b>About</b>:<br>\r\n	MPD Commander (mpdc) is a Windows and *nix graphical client/frontend.  It also supports cool Winamp skins!<br>\r\n	<br>\r\n	For more info: <a href=http://engineering.kabel.utwente.nl/mpdc/>http://engineering.kabel.utwente.nl/mpdc/</a><br>\r\n	<br>        \r\n	<b>Screenshots</b>:<br>\r\n	<a href=\"images/mpdc_1.png\">Screenshot 1</a><br>\r\n	<br>\r\n	<b>Install on Windows:</b><br>\r\n	1) <a href=?page=files#mpdc>download MPD Commander</a><br>\r\n	2) Run Setup Program<br>\r\n	<br>\r\n	<b>Install on Unix:</b><br>\r\n	1) <a href=?page=files#mpdc>download MPD Commander</a><br>\r\n	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n  	4) compile (type \"make\")<br>\r\n	5) (Optional) as root: make install<br>\r\n	<br>\r\n	(c)Copyright 2004 A.E. van Putten and A.J. Admiraal<br>\r\n	This code is released under the GPL license<br>\r\n	</p>\r\n','mpdc',1,NULL,1,1,2,2,'2004-06-13 23:10:01','2004-06-13 23:10:01');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (26,'WMmp','wmmp','	<p class=\"title\">WMmp</p>\r\n	<p><b>About</b>:<br>\r\n	A (Window Maker) dock app for interfacing MPD.<br>\r\n	(based on <a href=\"http://www.dockapps.com/file.php/id/172\">WMxmms</a>)<br>\r\n	<br>\r\n	<b>Screenshots</b>:<br>\r\n	<a href=\"images/WMmp_20030708_1.jpg\">WMmp Screenshot 1</a> (7/8/2003)<br>\r\n	<br>\r\n	<b>Install</b>:<br>\r\n  	1) <a href=?page=files#WMmp>download WMmp</a><br>\r\n  	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n  	4) compile (type \"make\")<br>\r\n	5) (Optional) as root: make install<br>\r\n	<br>\r\n	<b>Run</b>:<br>\r\n	1) Set environment variables MPD_HOST and MPD_PORT<br>\r\n	2) $ WMmp<br>\r\n	</p>\r\n','WMmp',1,NULL,1,1,2,2,'2004-06-13 23:10:48','2004-06-13 23:10:48');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (27,'mpcstick','mpcstick','	<p class=\"title\">mpcstick</p>\r\n	<p><b>About</b>:<br>\r\n	A MPD Linux Joystick Client.<br>\r\n	<br>\r\n	For support and more information, go <a href=http://aaronbock.net/software/mpcstick/>here</a>.<br>\r\n	<br>        \r\n	<b>Install:</b><br>\r\n	1) <a href=?page=files#mpcstick>download mpcstick</a><br>\r\n	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n	4) compile (type \"make\")<br>\r\n	5) as root: make install<br>\r\n	<br>\r\n	(c)Copyright 2004 Aaron Bockover<br>\r\n	This code is released under the GPL license<br>\r\n	</p>\r\n','mpcstick',1,NULL,1,1,2,2,'2004-06-13 23:11:31','2004-06-13 23:11:31');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (28,'bashmp','bashmp','	<p class=\"title\">bashmp</p>\r\n	<p><b>About</b>: MPD client written as bash aliases and functions. Includes a lightweight CGI interface.<br>\r\n	<br>\r\n	<b>Features</b>:<br>\r\n        * The smallest MPD client ever!<br>\r\n	* Requires only netcat (nc), sed and egrep<br>\r\n        * Does _NOT_ require a compiler<br>\r\n        * Includes mpcgi.sh which is a CGI interface<br>\r\n	<br>\r\n	<b>Install</b>:<br>\r\n  	1) <a href=?page=files#bashmp>download bashmp</a> and possibly mpcgi.sh<br>\r\n  	2) add bashmp.sh to your .bashrc or .profile or source it<br>\r\n	<br>\r\n	<b>Configuration</b>:<br>\r\n	Set the environment variables MPD_HOST and MPD_PORT in the script. This is particularly important\r\n	for running it as a CGI.<br>\r\n	<br>\r\n	<b>CGI Install</b>:<br>\r\n	1) Get or have a web server that supports CGI. <a href=\"http://www.acme.com/software/thttpd/\">thttpd</a> seems\r\n	like a good choice.<br>\r\n	2) Install bashmp.sh and mpcgi.sh into your cgi-bin directory.<br>\r\n	3) Fire up your web browser and point it at mpcgi.sh<br>\r\n	<br>\r\n	<b>For Help</b>:<br>\r\n	Read the script!<br>\r\n	<br>\r\n	<b>Thanks</b>:<br>\r\n	mpc for inspiration!<br>\r\n	</p>\r\n	<b>Sample of mpcgi.sh CGI:<b> (of course the links won\'t work for you!!)<br>\r\n<h3>\r\nControls [\r\n<a href=\"http://192.168.0.219:8090/cgi-bin/mpcgi?cmd=play\">Play</a> | <a href=\"http://192.168.0.219:8090/cgi-bin/mpcgi?cmd=stop\">Stop</a> | \r\n<a href=\"http://192.168.0.219:8090/cgi-bin/mpcgi?cmd=prev\">Prev</a> | <a href=\"http://192.168.0.219:8090/cgi-bin/mpcgi?cmd=next\">Next</a> ]\r\n<br>\r\nOptions [ <a href=\"?cmd=repeat\">Repeat</a> | <a href=\"?cmd=random\">Random</a> ]\r\n<br>\r\nVolume [\r\n<a href=\"http://192.168.0.219:8090/cgi-bin/mpcgi?cmd=vol0\">Mute</a> |\r\n<a href=\"http://192