INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (29,'arthist','arthist','	<p class=\"title\">arthist</p>\r\n	<p><b>About</b>:<br>\r\n	arthist client for MPD written in Perl which creates a web page that\'s\r\n	a blog of your MPD activity including inline album cover graphics\r\n	from Amazon<br>\r\n	<br>\r\n	<b>Samples</b>:<br>\r\n	<a href=\"http://www.studiosells.net/msells/arthist/large_sample.html\">\r\n	Large sample output</a> (712k)<br>\r\n	<a href=\"http://www.studiosells.net/msells/arthist/small_sample.html\">\r\n	Small sample output</a> (88k)<br>\r\n	<br>\r\n	<b>Install</b><br>\r\n	1) <a href=?page=files#arthist>Download arthist</a><br>\r\n	2) untar <b>tar xvf arthist.tar</b>.<br>\r\n	3) RTFM - README.txt or README.html<br>\r\n	4) Install necessary Perl modules:\r\n	<ul>\r\n	<li><b>perl -MCPAN -e \'install Net::Amazon\'</b>\r\n	<li><b>perl -MCPAN -e \'install CGI::Enurl\'</b>\r\n	</ul>\r\n	5) Edit arthist and make any configuration changes you want<br>\r\n	6) Run it! <b>cd arthist-<i>VERSION</i> ; ./arthist</b><br>\r\n	7) Open the output file in your web browser<br>\r\n','arthist',1,NULL,1,1,2,2,'2004-06-13 23:14:10','2004-06-13 23:14:10');
INSERT INTO cms_pages (page_id, page_title, page_url, page_content, menu_text, show_in_menu, default_page, owner, active, section_id, template_id, create_date, modified_date) VALUES (30,'gtk2mp','gtk2mp','	<p class=\"title\">gtk2mp</p>\r\n	<p><b>About</b>:<br>\r\n	A graphical client/frontend for MPD written with GTK2+.<br>\r\n	gtk2mp is <b>DEPRECATED</b>, try <a href=?page=gmpc>gmpc</a> instead.<br>\r\n	<br>\r\n	For more info: <a href=\"http://www.moviegalaxy.com.ar/gtk2mp/\">http://www.moviegalaxy.com.ar/gtk2mp/</a><br>\r\n	<br>        \r\n	<b>Screenshots</b>:<br>\r\n	<a href=\"images/gtk2mp.png\">Screenshot 1</a><br>\r\n	<br>\r\n	<b>Install:</b><br>\r\n	1) <a href=?page=files#gtk2mp>download gtk2mp</a><br>\r\n	2) untar and unzip<br>\r\n	3) configure (type \"./configure\")<br>\r\n	4) compile (type \"make\")<br>\r\n	5) (Optional) as root: make install<br>\r\n	<br>\r\n	(c)Copyright 2003 Rodrigo A. Diaz Leven a.k.a Bruj0<br>\r\n	This code is released under the GPL license<br>\r\n	</p>\r\n','gtk2mp',1,NULL,1,1,2,2,'2004-06-13 23:14:48','2004-06-13 23:14:48');

